,(97,12,'その他','miscellaneous','その他の便利なコマンド',NULL,3,7,1,'2026-01-24 16:35:29','2026-01-24 16:35:29'),(98,13,'はじめに','getting-started','YAMLの基本と導入',NULL,9,1,1,'2026-01-24 16:35:29','2026-01-24 16:35:29'),(99,13,'YAMLコレクション','collections','配列、マッピング、ネスト構造',NULL,9,2,1,'2026-01-24 16:35:29','2026-01-24 16:35:29'),(100,13,'YAMLリファレンス','reference','記号、エスケープ、型',NULL,14,3,1,'2026-01-24 16:35:29','2026-01-24 16:35:29'),(101,14,'はじめに','getting-started','MySQLへの接続と基本操作',NULL,5,1,1,'2026-01-24 16:35:29','2026-01-24 16:35:30'),(102,14,'テーブル管理','managing-tables','テーブルの作成・変更・削除',NULL,9,2,1,'2026-01-24 16:35:29','2026-01-24 16:35:30'),(103,14,'データの取得','querying-data','SELECT文とフィルタリング',NULL,8,3,1,'2026-01-24 16:35:29','2026-01-24 16:35:30'),(104,14,'結合','joins','テーブル間の結合',NULL,11,4,1,'2026-01-24 16:35:29','2026-01-24 16:35:30'),(105,14,'制約','constraints','SQL制約の使用',NULL,5,5,1,'2026-01-24 16:35:30','2026-01-24 16:35:30'),(106,14,'データの変更','modifying-data','INSERT、UPDATE、DELETE',NULL,7,6,1,'2026-01-24 16:35:30','2026-01-24 16:35:30'),(107,14,'ビュー','views','ビューの作成と管理',NULL,2,7,1,'2026-01-24 16:35:30','2026-01-24 16:35:30'),(108,14,'データ型','data-types','文字列、日付、数値型',NULL,3,8,1,'2026-01-24 16:35:30','2026-01-24 16:35:30'),(109,14,'関数と演算子','functions','MySQL関数の概要',NULL,7,9,1,'2026-01-24 16:35:30','2026-01-24 16:35:30');
