INSERT INTO `code_examples` VALUES (1,1,1,'hello.php','hellophp','<?php // begin with a PHP open tag.\n\necho \"Hello World\\n\";\nprint(\"Hello Kizamu.com\");\n\n?>','PHPの開始タグから始める','Hello World\nHello Kizamu.com','easy','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(2,1,1,'変数','','$boolean1 = true;\n$boolean2 = True;\n\n$int = 12;\n$float = 3.1415926;\nunset($float);  // Delete variable\n\n$str1 = \"How are you?\";\n$str2 = \'Fine, thanks\';','変数の宣言と型',NULL,'easy','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(3,1,1,'文字列','','$url = \"Kizamu.com\";\necho \"I\'m learning PHP at $url\";\n\n// Concatenate strings\necho \"I\'m learning PHP at \" . $url;\n\n$hello = \"Hello, \";\n$hello .= \"World!\";\necho $hello;','文字列操作と連結','I\'m learning PHP at Kizamu.com\nI\'m learning PHP at Kizamu.com\nHello, World!','easy','[\"php\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(4,1,1,'配列','','$num = [1, 3, 5, 7, 9];\n$num[5] = 11;\nunset($num[2]);    // Delete variable\nprint_r($num);\necho count($num);','配列操作',NULL,'easy','[\"php\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(5,1,1,'演算子','','$x = 1;\n$y = 2;\n\n$sum = $x + $y;\necho $sum;','基本的な算術演算子','3','easy','[\"php\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(6,1,1,'インクルード','','<?php\ninclude \'vars.php\';\necho $fruit . \"\\n\";\n\n/* Same as include,\ncause an error if cannot be included*/\nrequire \'vars.php\';\n\n// Also works\ninclude(\'vars.php\');\nrequire(\'vars.php\');','includeとrequire文',NULL,'easy','[\"php\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(7,1,1,'関数','','function add($num1, $num2 = 1) {\n    return $num1 + $num2;\n}\necho add(10);\necho add(10, 5);','デフォルトパラメータを持つ関数定義','11\n15','easy','[\"php\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(8,1,1,'コメント','','# This is a one line shell-style comment\n\n// This is a one line c++ style comment\n\n/* This is a multi line comment\n   yet another line of comment */','PHPのコメント構文',NULL,'easy','[\"php\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(9,1,1,'定数','','const MY_CONST = \"hello\";\n\necho MY_CONST;\n\necho \'MY_CONST is: \' . MY_CONST;','定数の宣言','hello\nMY_CONST is: hello','easy','[\"php\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(10,1,1,'クラス','','class Student {\n    public function __construct($name) {\n        $this->name = $name;\n    }\n}\n$alex = new Student(\"Alex\");','基本的なクラス定義とインスタンス化',NULL,'medium','[\"php\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(11,2,1,'ブール型','','$boolean1 = true;\n$boolean2 = TRUE;\n$boolean3 = false;\n$boolean4 = FALSE;\n\n$boolean5 = (boolean) 1;\n$boolean6 = (boolean) 0;','ブール型とキャスト',NULL,'easy','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(12,2,1,'整数型','','$int1 = 28;\n$int2 = -32;\n$int3 = 012;   // octal => 10\n$int4 = 0x0F;  // hex => 15\n$int5 = 0b101; // binary => 5\n\n$int6 = 2_000_100_000;  // PHP 7.4.0','整数型とフォーマット',NULL,'easy','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(13,2,1,'浮動小数点型','','$float1 = 1.234;\n$float2 = 1.2e7;\n$float3 = 7E-10;\n\n$float4 = 1_234.567;  // PHP 7.4.0\nvar_dump($float4);\n\n$float5 = 1 + \"10.5\";\n$float6 = 1 + \"-1.3e3\";','浮動小数点型/倍精度浮動小数点型と数値文字列',NULL,'easy','[\"php\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(14,2,1,'Null','null','$a = null;\n$b = \'Hello php!\';\necho $a ?? \'a is unset\';\necho $b ?? \'b is unset\';\n\n$a = array();\n$a == null;\n$a === null;\nis_null($a);','Null合体演算子とnullチェック','a is unset\nHello php','easy','[\"php\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(15,3,1,'文字列','','$sgl_quotes = \'$String\';\n\n$dbl_quotes = \"This is a $sgl_quotes.\";\n\n$escaped   = \"a \\t tab character.\";\n\n$unescaped = \'a slash and a t: \\t\';','シングルクォートとダブルクォート',NULL,'easy','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(16,3,1,'複数行','','$str = \"foo\";\n\n// Uninterpolated multi-liners\n$nowdoc = <<<\'END\'\nMulti line string\n$str\nEND;\n\n// Will do string interpolation\n$heredoc = <<<END\nMulti line\n$str\nEND;','HeredocとNowdoc構文',NULL,'medium','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(17,3,1,'操作','','$s = \"Hello Phper\";\necho strlen($s);\n\necho substr($s, 0, 3);\necho substr($s, 1);\necho substr($s, -4, 3);\n\necho strtoupper($s);\necho strtolower($s);\n\necho strpos($s, \"l\");\nvar_dump(strpos($s, \"L\"));','一般的な文字列関数',NULL,'easy','[\"php\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(18,4,1,'定義','','$a1 = [\"hello\", \"world\", \"!\"];\n$a2 = array(\"hello\", \"world\", \"!\");\n$a3 = explode(\",\", \"apple,pear,peach\");','配列を定義する様々な方法',NULL,'easy','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(19,4,1,'混合キー','','$array = array(\n    \"foo\" => \"bar\",\n    \"bar\" => \"foo\",\n    100   => -100,\n    -100  => 100,\n);\nvar_dump($array);','整数と文字列キーが混在する配列',NULL,'easy','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(20,4,1,'多次元配列','cny','$multiArray = [ \n    [1, 2, 3],\n    [4, 5, 6],\n    [7, 8, 9],\n];\n\nprint_r($multiArray[0][0]);\nprint_r($multiArray[0][1]);\nprint_r($multiArray[0][2]);','多次元配列',NULL,'easy','[\"php\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(21,4,1,'配列操作','','$arr = array(5 => 1, 12 => 2);\n$arr[] = 56;      // Append\n$arr[\"x\"] = 42;   // Add with key\nsort($arr);       // Sort\nunset($arr[5]);   // Remove\nunset($arr);      // Remove all','配列操作関数',NULL,'easy','[\"php\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(22,4,1,'Foreach反復','foreach','$colors = array(\'red\', \'blue\', \'green\');\n\nforeach ($colors as $color) {\n    echo \"Do you like $color?\\n\";\n}','Foreachループの反復処理',NULL,'easy','[\"php\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(23,4,1,'キー・値の反復','','$arr = [\"foo\" => \"bar\", \"bar\" => \"foo\"];\n\nforeach ( $arr as $key => $value )\n{\n    echo \"$key => $value\\n\";\n}','キー・値のペアを反復処理',NULL,'easy','[\"php\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(24,5,1,'戻り値','','function square($x)\n{\n    return $x * $x;\n}\n\necho square(4);','戻り値を持つ基本的な関数','16','easy','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(25,5,1,'戻り値の型','','// Basic return type declaration\nfunction sum($a, $b): float {\n    return $a + $b;\n}\nfunction get_item(): string {\n    return \"item\";\n}\n\nclass C {}\n// Returning an object\nfunction getC(): C { \n    return new C; \n}','戻り値の型宣言',NULL,'medium','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(26,5,1,'デフォルトパラメータ','','function coffee($type = \"cappuccino\")\n{\n    return \"Making a cup of $type.\\n\";\n}\necho coffee();\necho coffee(null);\necho coffee(\"espresso\");','デフォルトパラメータを持つ関数','Making a cup of cappuccino.\nMaking a cup of .\nMaking a cup of espresso.','easy','[\"php\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(27,5,1,'無名関数','','$greet = function($name)\n{\n    printf(\"Hello %s\\r\\n\", $name);\n};\n\n$greet(\'World\');\n$greet(\'PHP\');','無名関数（クロージャ）','Hello World\nHello PHP','medium','[\"php\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(28,5,1,'アロー関数','','$y = 1;\n \n$fn1 = fn($x) => $x + $y;\n\n// equivalent to using $y by value:\n$fn2 = function ($x) use ($y) {\n    return $x + $y;\n};\necho $fn1(5);\necho $fn2(5);','アロー関数（PHP 7.4+）','6\n6','medium','[\"php\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(29,6,1,'コンストラクタ','','class Student {\n    public function __construct($name) {\n        $this->name = $name;\n    }\n    public function print() {\n        echo \"Name: \" . $this->name;\n    }\n}\n$alex = new Student(\"Alex\");\n$alex->print();','クラスのコンストラクタとメソッド','Name: Alex','medium','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(30,6,1,'継承','','class ExtendClass extends SimpleClass\n{\n    // Redefine the parent method\n    function displayVar()\n    {\n        echo \"Extending class\\n\";\n        parent::displayVar();\n    }\n}\n\n$extended = new ExtendClass();\n$extended->displayVar();','クラスの継承',NULL,'medium','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(31,6,1,'クラス変数','','class MyClass\n{\n    const MY_CONST       = \'value\';\n    static $staticVar    = \'static\';\n\n    // Visibility\n    public static $var1  = \'pubs\';\n    private static $var2 = \'pris\';\n    protected static $var3 = \'pros\';\n}\n\n// Access statically\necho MyClass::MY_CONST;\necho MyClass::$staticVar;','クラス定数と静的変数','value\\nstatic','medium','[\"php\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(32,6,1,'インターフェース','','interface Foo \n{\n    public function doSomething();\n}\ninterface Bar\n{\n    public function doSomethingElse();\n}\nclass Cls implements Foo, Bar \n{\n    public function doSomething() {}\n    public function doSomethingElse() {}\n}','インターフェースと複数の実装',NULL,'medium','[\"php\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(33,7,1,'エラーハンドリング','','try {\n    // Do something\n} catch (Exception $e) {\n    // Handle exception\n} finally {\n    echo \"Always print!\";\n}','Try-catch-finallyブロック',NULL,'medium','[\"php\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(34,7,1,'正規表現','','$str = \"Visit Kizamu.com\";\necho preg_match(\"/do/i\", $str);','preg_matchを使った正規表現','1','medium','[\"php\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:20','2026-01-24 16:35:20'),(35,8,2,'Hello.java','hellojava','public class Hello {\n  public static void main(String[] args) {\n    System.out.println(\"Hello, world!\");\n  }\n}','mainメソッドとコンソール出力を含む基本的なJavaプログラム構造','Hello, world!','easy','[\"