rintf(\"%v\\n\", p)                        // => {1 2}\n	fmt.Printf(\"%+v\\n\", p)                       // => {x:1 y:2}\n	fmt.Printf(\"%#v\\n\", p)                       // => main.point{x:1, y:2}\n	fmt.Printf(\"%T\\n\", p)                        // => main.point\n	fmt.Printf(\"%t\\n\", true)                     // => TRUE\n	fmt.Printf(\"%d\\n\", 123)                      // => 123\n	fmt.Printf(\"%b\\n\", 14)                       // => 1110\n	fmt.Printf(\"%c\\n\", 33)                       // => !\n	fmt.Printf(\"%x\\n\", 456)                      // => 1c8\n	fmt.Printf(\"%f\\n\", 78.9)                     // => 78.9\n	fmt.Printf(\"%e\\n\", 123400000.0)              // => 1.23E+08\n	fmt.Printf(\"%E\\n\", 123400000.0)              // => 1.23E+08\n	fmt.Printf(\"%s\\n\", \"\\\"string\\\"\")             // => \"string\"\n	fmt.Printf(\"%q\\n\", \"\\\"string\\\"\")             // => \"\\\"string\\\"\"\n	fmt.Printf(\"%x\\n\", \"hex this\")               // => 6.86578E+15\n	fmt.Printf(\"%p\\n\", &p)                       // => 0xc00002c040\n	fmt.Printf(\"|%6d|%6d|\\n\", 12, 345)           // => |    12|   345|\n	fmt.Printf(\"|%6.2f|%6.2f|\\n\", 1.2, 3.45)     // => |  1.20|  3.45|\n	fmt.Printf(\"|%-6.2f|%-6.2f|\\n\", 1.2, 3.45)   // => |1.20  |3.45  |\n	fmt.Printf(\"|%6s|%6s|\\n\", \"foo\", \"b\")        // => |   foo|     b|\n	fmt.Printf(\"|%-6s|%-6s|\\n\", \"foo\", \"b\")      // => |foo   |b     |\n\n	s := fmt.Sprintf(\"a %s\", \"string\")\n	fmt.Println(s)\n\n	fmt.Fprintf(os.Stderr, \"an %s\\n\", \"error\")\n}','fmt.Printfによるフォーマット出力','{1 2}\n{x:1 y:2}\nmain.point{x:1, y:2}\nmain.point\ntrue\n123\n1110\n!\n1c8\n78.9\n1.23E+08\n1.23E+08\n\"string\"\n\"\\\"string\\\"\"\n6865782074686973\n0xc00002c040\n|    12|   345|\n|  1.20|  3.45|\n|1.20  |3.45  |\n|   foo|     b|\n|foo   |b     |\na string\nan error','medium','[\"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:21','2026-01-24 16:35:21'),(98,18,3,'複数の戻り値','','func swap(x, y string) (string, string) {\n    return y, x\n}\n\na, b := swap(\"hello\", \"world\")\nfmt.Println(a, b)','複数の戻り値を持つ関数','world hello','easy','[\"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:21','2026-01-24 16:35:21'),(99,18,3,'名前付き戻り値','','func split(sum int) (x, y int) {\n    x = sum * 4 / 9\n    y = sum - x\n    return\n}','名前付き戻り値',NULL,'medium','[\"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:21','2026-01-24 16:35:21'),(100,18,3,'可変長引数関数','','func sum(nums ...int) int {\n    total := 0\n    for _, num := range nums {\n        total += num\n    }\n    return total\n}\n\nfmt.Println(sum(1, 2, 3))','可変長引数関数','6','medium','[\"go\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(101,18,3,'関数を値として','','func main() {\n    // assign a function to a name\n    add := func(a, b int) int {\n        return a + b\n    }\n    // use the name to call the function\n    fmt.Println(add(3, 4)) // => 7\n}','第一級関数としての関数','7','medium','[\"go\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(102,18,3,'クロージャ','','func scope() func() int{\n    outer_var := 2\n    foo := func() int {return outer_var}\n    return foo\n}\n\n// Outputs: 2\nfmt.Println(scope()())','クロージャの例','2','medium','[\"go\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(103,19,3,'インポート','','import \"fmt\"\nimport \"math/rand\"','基本的なインポート',NULL,'easy','[\"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(104,19,3,'インポートブロック','','import (\n  \"fmt\"        // gives fmt.Println\n  \"math/rand\"  // gives rand.Intn\n)','インポートブロック構文',NULL,'easy','[\"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(105,19,3,'エイリアス','','import r \"math/rand\"\n\nr.Intn()','インポートエイリアス',NULL,'easy','[\"go\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(106,19,3,'名前のエクスポート','','// Begin with a capital letter\nfunc Hello () {\n  ···\n}','エクスポートされた名前',NULL,'easy','[\"go\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(107,20,3,'Goroutine','goroutine','package main\n\nimport (\n	\"fmt\"\n	\"time\"\n)\n\nfunc f(from string) {\n	for i := 0; i < 3; i++ {\n		fmt.Println(from, \":\", i)\n	}\n}\n\nfunc main() {\n	f(\"direct\")\n	go f(\"goroutine\")\n\n	go func(msg string) {\n		fmt.Println(msg)\n	}(\"going\")\n\n	time.Sleep(time.Second)\n	fmt.Println(\"done\")\n}','Goroutineの基本','direct : 0\ndirect : 1\ndirect : 2\ngoing\ngoroutine : 0\ngoroutine : 1\ngoroutine : 2\ndone','medium','[\"concurrency\", \"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(108,20,3,'WaitGroup','waitgroup','package main\n\nimport (\n	\"fmt\"\n	\"sync\"\n	\"time\"\n)\n\nfunc w(id int, wg *sync.WaitGroup) {\n	defer wg.Done()\n	fmt.Printf(\"%d starting\\n\", id)\n\n	time.Sleep(time.Second)\n	fmt.Printf(\"%d done\\n\", id)\n}\n\nfunc main() {\n	var wg sync.WaitGroup\n	for i := 1; i <= 5; i++ {\n		wg.Add(1)\n		go w(i, &wg)\n	}\n	wg.Wait()\n}','同期のためのWaitGroup','1 starting\n2 starting\n3 starting\n4 starting\n5 starting\n1 done\n2 done\n3 done\n4 done\n5 done','medium','[\"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(109,20,3,'チャネルのクローズ','','ch <- 1\nch <- 2\nch <- 3\nclose(ch) // Closes a channel\n\n// Iterate the channel until closed\nfor i := range ch {\n  ···\n}\n\n// Closed if `ok == false`\nv, ok := <- ch','チャネルのクローズとイテレーション',NULL,'medium','[\"go\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(110,20,3,'バッファ付きチャネル','','ch := make(chan int, 2)\nch <- 1\nch <- 2\nch <- 3\n// fatal error:\n// all goroutines are asleep - deadlock','バッファ付きチャネル',NULL,'medium','[\"go\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(111,21,3,'関数の遅延実行','','func main() {\n  defer func() {\n    fmt.Println(\"Done\")\n  }()\n  fmt.Println(\"Working...\")\n}','Defer文','Working...\nDone','easy','[\"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(112,21,3,'Defer','defer','func main() {\n  defer fmt.Println(\"Done\")\n  fmt.Println(\"Working...\")\n}','シンプルなdefer','Working...\nDone','easy','[\"error-handling\", \"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(113,22,3,'レシーバ','','type Vertex struct {\n  X, Y float64\n}\n\nfunc (v Vertex) Abs() float64 {\n  return math.Sqrt(v.X * v.X + v.Y * v.Y)\n}\n\nv := Vertex{1, 2}\nv.Abs()','値レシーバを持つメソッド',NULL,'medium','[\"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(114,22,3,'変更','','func (v *Vertex) Scale(f float64) {\n  v.X = v.X * f\n  v.Y = v.Y * f\n}\n\nv := Vertex{6, 12}\nv.Scale(0.5)\n// `v` is updated','ポインタレシーバを持つメソッド',NULL,'medium','[\"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(115,23,3,'基本的なインターフェース','','type Shape interface {\n  Area() float64\n  Perimeter() float64\n}','インターフェースの定義',NULL,'medium','[\"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(116,23,3,'構造体','','type Rectangle struct {\n  Length, Width float64\n}','構造体の定義',NULL,'easy','[\"go\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(117,23,3,'メソッド','','func (r Rectangle) Area() float64 {\n  return r.Length * r.Width\n}\n\nfunc (r Rectangle) Perimeter() float64 {\n  return 2 * (r.Length + r.Width)\n}','インターフェースメソッドの実装',NULL,'medium','[\"go\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(118,23,3,'インターフェースの例','','func main() {\n  var r Shape = Rectangle{Length: 3, Width: 4}\n  fmt.Printf(\"Type of r: %T, Area: %v, Perimeter: %v.\", r, r.Area(), r.Perimeter())\n}','インターフェースの使用','Type of r: main.Rectangle, Area: 12, Perimeter: 14.','medium','[\"go\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(119,24,3,'キーワード','','break default func interface select\ncase defer go map struct\nchan else goto package switch\nconst fallthrough if range type\ncontinue for import return var','Goキーワード',NULL,'easy','[\"go\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(120,25,4,'Console','console','// => Hello world!\nconsole.log(\'Hello world!\');\n\n// => Hello Kizamu\nconsole.warn(\'hello %s\', \'Kizamu\');\n\n// Prints error message to stderr\nconsole.error(new Error(\'Oops!\'));','出力用のコンソールメソッド','Hello world!\nHello Kizamu\nError: Oops!','easy','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(121,25,4,'数値','','let amount = 6;\nlet price = 4.99;','数値の宣言',NULL,'easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(122,25,4,'変数','','let x = null;\nlet name = \"Tammy\";\nconst found = false;\n\n// => Tammy, false, null\nconsole.log(name, found, x);\n\nvar a;\nconsole.log(a); // => undefined','let、const、varによる変数宣言','Tammy false null\nundefined','easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(123,25,4,'文字列','','let single = \'Wheres my bandit hat?\';\nlet double = \"Wheres my bandit hat?\";\n\n// => 21\nconsole.log(single.length);','文字列の宣言とプロパティ','21','easy','[\"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(124,25,4,'算術演算子','','5 + 5 = 10     // Addition\n10 - 5 = 5     // Subtraction\n5 * 10 = 50    // Multiplication\n10 / 5 = 2     // Division\n10 % 5 = 0     // Modulo','基本的な算術演算子',NULL,'easy','[\"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(125,25,4,'コメント','','// This line will denote a comment\n\n/*  \nThe below configuration must be \nchanged before deployment. \n*/','コメント構文',NULL,'easy','[\"javascript\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(126,25,4,'代入演算子','','let number = 100;\n\n// Both statements will add 10\nnumber = number + 10;\nnumber += 10;\n\nconsole.log(number); \n// => 120','代入演算子','120','easy','[\"javascript\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(127,25,4,'文字列補間','','let age = 7;\n\n// String concatenation\n\'Tommy is \' + age + \' years old.\';\n\n// String interpolation\n`Tommy is ${age} years old.`;','文字列連結とテンプレートリテラル',NULL,'easy','[\"javascript\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(128,25,4,'letキーワード','let','let count; \nconsole.log(count); // => undefined\ncount = 10;\nconsole.log(count); // => 10','letキーワードの使用','undefined\n10','easy','[\"scope\", \"javascript\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(129,25,4,'constキーワード','const','const numberOfColumns = 4;\n\n// TypeError: Assignment to constant...\nnumberOfColumns = 8;','constキーワード - 不変のバインディング',NULL,'easy','[\"scope\", \"javascript\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(130,26,4,'if文','if','const isMailSent = true;\n\nif (isMailSent) {\n  console.log(\'Mail sent to recipient\');\n}','基本的なif文','Mail sent to recipient','easy','[\"conditional\", \"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(131,26,4,'三項演算子','','var x=1;\n\n// => true\nresult = (x == 1) ? true : false;','三項条件演算子',NULL,'easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(132,26,4,'論理演算子 ||','','true || false;       // true\n10 > 5 || 10 > 20;   // true\nfalse || false;      // false\n10 > 100 || 10 > 20; // false','論理OR演算子',NULL,'easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(133,26,4,'論理演算子 &&','','true && true;        // true\n1 > 2 && 2 > 1;      // false\ntrue && false;       // false\n4 === 4 && 3 > 1;    // true','論理AND演算子',NULL,'easy','[\"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(134,26,4,'比較演算子','','1 > 3                // false\n3 > 1                // true\n250 >= 250           // true\n1 === 1              // true\n1 === 2              // false\n1 === \'1\'            // false','比較演算子',NULL,'easy','[\"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(135,26,4,'論理演算子 !','','let lateToWork = true;\nlet oppositeValue = !lateToWork;\n\n// => false\nconsole.log(oppositeValue);','論理NOT演算子','false','easy','[\"javascript\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(136,26,4,'Null合体演算子 ??','null','null ?? \'I win\';           //  \'I win\'\nundefined ?? \'Me too\';     //  \'Me too\'\n\nfalse ?? \'I lose\'          //  false\n0 ?? \'I lose again\'        //  0\n\'\' ?? \'Damn it\'            //  \'\'','Null合体演算子',NULL,'medium','[\"javascript\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(137,26,4,'else if','else-if','const size = 10;\n\nif (size > 100) {\n  console.log(\'Big\');\n} else if (size > 20) {\n  console.log(\'Medium\');\n} else if (size > 4) {\n  console.log(\'Small\');\n} else {\n  console.log(\'Tiny\');\n}\n// Print: Small','else ifチェーン','Small','easy','[\"conditional\", \"javascript\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(138,26,4,'switch文','switch','const food = \'salad\';\n\nswitch (food) {\n  case \'oyster\':\n    console.log(\'The taste of the sea\');\n    break;\n  case \'pizza\':\n    console.log(\'A delicious pie\');\n    break;\n  default:\n    console.log(\'Enjoy your meal\');\n}','switch文','Enjoy your meal','easy','[\"conditional\", \"javascript\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(139,26,4,'== vs ===','vs','0 == false   // true\n0 === false  // false, different type\n1 == \"1\"     // true,  automatic type conversion \n1 === \"1\"    // false, different type\nnull == undefined  // true\nnull === undefined // false\n\'0\' == false       // true\n\'0\' === false      // false','等価演算子の比較',NULL,'easy','[\"javascript\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(140,27,4,'関数','','// Defining the function:\nfunction sum(num1, num2) {\n  return num1 + num2;\n}\n\n// Calling the function:\nsum(3, 6); // 9','関数宣言',NULL,'easy','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(141,27,4,'無名関数','','// Named function\nfunction rocketToMars() {\n  return \'BOOM!\';\n}\n\n// Anonymous function\nconst rocketToMars = function() {\n  return \'BOOM!\';\n}','名前付き関数と無名関数',NULL,'easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(142,27,4,'アロー関数 - 2つの引数','2','const sum = (param1, param2) => { \n  return param1 + param2; \n}; \nconsole.log(sum(2,5)); // => 7','2つのパラメータを持つアロー関数','7','easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(143,27,4,'アロー関数 - 引数なし','','const printHello = () => { \n  console.log(\'hello\'); \n}; \nprintHello(); // => hello','パラメータなしのアロー関数','hello','easy','[\"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(144,27,4,'アロー関数 - 単一引数','','const checkWeight = weight => { \n  console.log(`Weight : ${weight}`); \n}; \ncheckWeight(25); // => Weight : 25','単一パラメータのアロー関数','Weight : 25','easy','[\"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(145,27,4,'簡潔なアロー関数','','const multiply = (a, b) => a * b; \n// => 60 \nconsole.log(multiply(2, 30));','簡潔なアロー関数構文','60','easy','[\"javascript\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(146,27,4,'returnキーワード','return','// With return\nfunction sum(num1, num2) {\n  return num1 + num2;\n}\n\n// The function doesn\'t output the sum\nfunction sum(num1, num2) {\n  num1 + num2;\n}','returnキーワードの重要性',NULL,'easy','[\"javascript\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:22','2026-01-24 16:35:22'),(147,27,4,'関数の呼び出し','','// Defining the function\nfunction sum(num1, num2) {\n  return num1 + num2;\n}\n\n// Calling the function\nsum(2, 4); // 6','関数の呼び出し',NULL,'easy','[\"javascript\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(148,27,4,'関数式','','const dog = function() {\n  return \'Woof!\';\n}','関数式',NULL,'easy','[\"javascript\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(149,27,4,'関数パラメータ','','// The parameter is name\nfunction sayHello(name) {\n  return `Hello, ${name}!`;\n}','関数パラメータ',NULL,'easy','[\"javascript\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(150,27,4,'関数宣言','','function add(num1, num2) {\n  return num1 + num2;\n}','関数宣言構文',NULL,'easy','[\"javascript\", \"basics\"]',0,0,11,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(151,28,4,'スコープ','','function myFunction() { \n  \n  var pizzaName = \"Margarita\";\n  // Code here can use pizzaName\n  \n}\n\n// Code here can\'t use pizzaName','関数スコープ',NULL,'easy','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(152,28,4,'ブロックスコープ変数','','const isLoggedIn = true;\n\nif (isLoggedIn == true) {\n  const statusMessage = \'Logged in.\';\n}\n\n// Uncaught ReferenceError...\nconsole.log(statusMessage);','constによるブロックスコープ',NULL,'easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(153,28,4,'グローバル変数','','// Variable declared globally\nconst color = \'blue\';\n\nfunction printColor() {\n  console.log(color);\n}\n\nprintColor(); // => blue','グローバルスコープ','blue','easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(154,28,4,'let vs var','let-vs-var','for (let i = 0; i < 3; i++) {\n  // This is the Max Scope for \'let\'\n  // i accessible ✔️\n}\n// i not accessible ❌\n\nfor (var i = 0; i < 3; i++) {\n  // i accessible ✔️\n}\n// i accessible ✔️','letとvarのスコープの違い',NULL,'medium','[\"scope\", \"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(155,28,4,'クロージャ付きループ','','// Prints 3 thrice, not what we meant.\nfor (var i = 0; i < 3; i++) {\n  setTimeout(_ => console.log(i), 10);\n}\n\n// Prints 0, 1 and 2, as expected.\nfor (let j = 0; j < 3; j++) { \n  setTimeout(_ => console.log(j), 10);\n}','varとletでのクロージャの動作','3\n3\n3\n0\n1\n2','medium','[\"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(156,29,4,'配列','','const fruits = [\"apple\", \"orange\", \"banana\"];\n\n// Different data types\nconst data = [1, \'chicken\', false];','配列の宣言',NULL,'easy','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(157,29,4,'プロパティ .length','length','const numbers = [1, 2, 3, 4];\n\nconsole.log(numbers.length); // => 4','配列のlengthプロパティ','4','easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(158,29,4,'要素へのアクセス','','const fruits = [\"apple\", \"orange\", \"banana\"];\n\nconsole.log(fruits[0]); // => apple\nconsole.log(fruits[1]); // => orange','インデックスによる配列要素へのアクセス','apple\norange','easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(159,29,4,'.push() メソッド','push','const items = [\'pencil\', \'notebook\', \'eraser\'];\nitems.push(\'backpack\');\nconsole.log(items);','配列の末尾に要素を追加','[\'pencil\', \'notebook\', \'eraser\', \'backpack\']','easy','[\"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(160,29,4,'.pop() メソッド','pop','const items = [\'pencil\', \'notebook\', \'eraser\'];\nconst removedItem = items.pop();\nconsole.log(removedItem); // => eraser','配列の最後の要素を削除','eraser','easy','[\"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(161,29,4,'.shift() メソッド','shift','const items = [\'pencil\', \'notebook\', \'eraser\'];\nconst firstItem = items.shift();\nconsole.log(firstItem); // => pencil','配列の最初の要素を削除','pencil','easy','[\"conditional\", \"javascript\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(162,29,4,'.unshift() メソッド','unshift','const items = [\'pencil\', \'notebook\'];\nitems.unshift(\'eraser\');\nconsole.log(items);','配列の先頭に要素を追加','[\'eraser\', \'pencil\', \'notebook\']','easy','[\"conditional\", \"javascript\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(163,29,4,'.slice() メソッド','slice','const fruits = [\'apple\', \'orange\', \'banana\', \'mango\'];\nconst citrus = fruits.slice(1, 3);\nconsole.log(citrus); // => [\'orange\', \'banana\']','配列の一部を抽出','[\'orange\', \'banana\']','easy','[\"javascript\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(164,29,4,'.splice() メソッド','splice','const fruits = [\'apple\', \'orange\', \'banana\'];\nfruits.splice(1, 1, \'mango\');\nconsole.log(fruits); // => [\'apple\', \'mango\', \'banana\']','要素の削除と挿入','[\'apple\', \'mango\', \'banana\']','medium','[\"javascript\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(165,29,4,'.forEach() メソッド','foreach','const fruits = [\'apple\', \'orange\', \'banana\'];\nfruits.forEach(fruit => {\n  console.log(fruit);\n});','配列要素の反復処理','apple\norange\nbanana','easy','[\"loop\", \"javascript\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(166,29,4,'.map() メソッド','map','const numbers = [1, 2, 3];\nconst doubled = numbers.map(num => num * 2);\nconsole.log(doubled); // => [2, 4, 6]','配列要素の変換','[2, 4, 6]','easy','[\"javascript\", \"basics\"]',0,0,11,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(167,29,4,'.filter() メソッド','filter','const numbers = [1, 2, 3, 4, 5];\nconst evens = numbers.filter(num => num % 2 === 0);\nconsole.log(evens); // => [2, 4]','配列要素のフィルタリング','[2, 4]','easy','[\"javascript\", \"basics\"]',0,0,12,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(168,29,4,'.reduce() メソッド','reduce','const numbers = [1, 2, 3, 4];\nconst sum = numbers.reduce((acc, num) => acc + num, 0);\nconsole.log(sum); // => 10','配列を単一の値に縮約','10','medium','[\"javascript\", \"basics\"]',0,0,13,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(169,30,4,'オブジェクトリテラル','','const person = {\n  name: \'John\',\n  age: 30,\n  city: \'New York\'\n};','オブジェクトリテラル構文',NULL,'easy','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(170,30,4,'プロパティへのアクセス','','const person = { name: \'John\', age: 30 };\n\nconsole.log(person.name);     // => John\nconsole.log(person[\'age\']);   // => 30','オブジェクトプロパティへのアクセス','John\n30','easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(171,30,4,'プロパティの追加','','const person = { name: \'John\' };\nperson.age = 30;\nperson[\'city\'] = \'New York\';\nconsole.log(person);','オブジェクトへのプロパティの追加','{ name: \'John\', age: 30, city: \'New York\' }','easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(172,30,4,'オブジェクトメソッド','','const person = {\n  name: \'John\',\n  greet: function() {\n    return `Hello, I\'m ${this.name}`;\n  }\n};\nconsole.log(person.greet());','オブジェクトメソッド','Hello, I\'m John','easy','[\"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(173,30,4,'Object.keys()','objectkeys','const person = { name: \'John\', age: 30 };\nconst keys = Object.keys(person);\nconsole.log(keys); // => [\'name\', \'age\']','オブジェクトのキーを取得','[\'name\', \'age\']','easy','[\"oop\", \"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(174,30,4,'Object.values()','objectvalues','const person = { name: \'John\', age: 30 };\nconst values = Object.values(person);\nconsole.log(values); // => [\'John\', 30]','オブジェクトの値を取得','[\'John\', 30]','easy','[\"oop\", \"javascript\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(175,31,4,'forループ','for','for (let i = 0; i < 5; i++) {\n  console.log(i);\n}','基本的なforループ','0\n1\n2\n3\n4','easy','[\"loop\", \"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(176,31,4,'for...ofループ','forof','const fruits = [\'apple\', \'orange\', \'banana\'];\nfor (const fruit of fruits) {\n  console.log(fruit);\n}','反復可能オブジェクト用のfor...ofループ','apple\norange\nbanana','easy','[\"loop\", \"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(177,31,4,'for...inループ','forin','const person = { name: \'John\', age: 30 };\nfor (const key in person) {\n  console.log(key, person[key]);\n}','オブジェクト用のfor...inループ','name John\nage 30','easy','[\"loop\", \"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(178,31,4,'whileループ','while','let i = 0;\nwhile (i < 5) {\n  console.log(i);\n  i++;\n}','whileループ','0\n1\n2\n3\n4','easy','[\"loop\", \"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(179,31,4,'do...whileループ','dowhile','let i = 0;\ndo {\n  console.log(i);\n  i++;\n} while (i < 5);','do...whileループ','0\n1\n2\n3\n4','easy','[\"loop\", \"javascript\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(180,32,4,'クラス宣言','','class Person {\n  constructor(name) {\n    this.name = name;\n  }\n  \n  greet() {\n    return `Hello, I\'m ${this.name}`;\n  }\n}\n\nconst john = new Person(\'John\');\nconsole.log(john.greet());','クラス宣言とインスタンス化','Hello, I\'m John','medium','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(181,32,4,'クラス継承','','class Animal {\n  constructor(name) {\n    this.name = name;\n  }\n}\n\nclass Dog extends Animal {\n  bark() {\n    return \'Woof!\';\n  }\n}\n\nconst dog = new Dog(\'Buddy\');\nconsole.log(dog.name);','extendsによるクラス継承','Buddy','medium','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(182,33,4,'Promiseの作成','promise','const promise = new Promise((resolve, reject) => {\n  if (res) {\n    resolve(\'Resolved!\');\n  }\n  else {\n    reject(Error(\'Error\'));\n  }\n});\n\npromise.then((res) => console.log(res), (err) => console.error(err));','Promiseの作成と使用',NULL,'medium','[\"async\", \"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(183,33,4,'.then() メソッド','then','const promise = new Promise((resolve, reject) => {    \n  setTimeout(() => {\n    resolve(\'Result\');\n  }, 200);\n});\n\npromise.then((res) => {\n  console.log(res);\n}, (err) => {\n  console.error(err);\n});','Promiseのthenメソッド','Result','medium','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(184,33,4,'.catch() メソッド','catch','const promise = new Promise((resolve, reject) => {  \n  setTimeout(() => {\n    reject(Error(\'Promise Rejected Unconditionally.\'));\n  }, 1000);\n});\n\npromise.catch((err) => {\n  console.error(err);\n});','Promiseのcatchメソッド','Error: Promise Rejected Unconditionally.','medium','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(185,33,4,'Promise.all()','promiseall','const promise1 = new Promise((resolve, reject) => {\n  setTimeout(() => {\n    resolve(3);\n  }, 300);\n});\nconst promise2 = new Promise((resolve, reject) => {\n  setTimeout(() => {\n    resolve(2);\n  }, 200);\n});\n\nPromise.all([promise1, promise2]).then((res) => {\n  console.log(res[0]);\n  console.log(res[1]);\n});','複数のPromise用のPromise.all','3\n2','medium','[\"async\", \"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(186,34,4,'Async関数','async','function helloWorld() {\n  return new Promise(resolve => {\n    setTimeout(() => {\n      resolve(\'Hello World!\');\n    }, 2000);\n  });\n}\n\nasync function msg() {\n  const msg = await helloWorld();\n  console.log(\'Message:\', msg);\n}\n\nmsg(); // Message: Hello World! <-- after 2 seconds','Async/awaitの基本','Message: Hello World!','medium','[\"async\", \"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(187,34,4,'エラーハンドリング','','let json = \'{ \"age\": 30 }\'; // incomplete data\n\ntry {\n  let user = JSON.parse(json); // <-- no errors\n  console.log( user.name ); // no name!\n} catch (e) {\n  console.error( \"Invalid JSON data!\" );\n}','Try-catchエラーハンドリング','undefined','easy','[\"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(188,35,4,'JSON','json','const jsonObj = {\n  \"name\": \"Rick\",\n  \"id\": \"11A\",\n  \"level\": 4  \n};','JSONオブジェクト構文',NULL,'easy','[\"javascript\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(189,35,4,'fetch API','fetch-api','fetch(url, {\n    method: \'POST\',\n    headers: {\n      \'Content-type\': \'application/json\',\n      \'apikey\': apiKey\n    },\n    body: data\n  }).then(response => {\n    if (response.ok) {\n      return response.json();\n    }\n    throw new Error(\'Request failed!\');\n  }, networkError => {\n    console.log(networkError.message)\n  })','Fetch API POSTリクエスト',NULL,'medium','[\"http\", \"javascript\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(190,35,4,'JSONフォーマット','json','fetch(\'url-that-returns-JSON\')\n.then(response => response.json())\n.then(jsonResponse => {\n  console.log(jsonResponse);\n});','JSONレスポンス付きFetch API',NULL,'easy','[\"javascript\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(191,35,4,'async await構文','async-await','const getSuggestions = async () => {\n  const wordQuery = inputField.value;\n  const endpoint = `${url}${queryParams}${wordQuery}`;\n  try{\n    const response = await fetch(endpoint, {cache: \'no-cache\'});\n    if(response.ok){\n      const jsonResponse = await response.json()\n    }\n  }\n  catch(error){\n    console.log(error)\n  }\n}','async/await付きFetch API',NULL,'medium','[\"async\", \"javascript\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:23','2026-01-24 16:35:23'),(192,36,5,'Hello World','hello-world','>>> print(\"Hello, World!\")\nHello, World!','Pythonで有名な「Hello World」プログラム','Hello, World!','easy','[\"python\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(193,36,5,'変数','','age = 18      # age is of type int\nname = \"John\" # name is now of type str\nprint(name)','Pythonは代入なしで変数を宣言できません','John','easy','[\"python\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(194,36,5,'データ型','','| str                          | Text     |\n| ---------------------------- | -------- |\n| int, float, complex          | Numeric  |\n| list, tuple, range           | Sequence |\n| dict                         | Mapping  |\n| set, frozenset               | Set      |\n| bool                         | Boolean  |\n| bytes, bytearray, memoryview | Binary   |','Pythonの組み込みデータ型',NULL,'easy','[\"python\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(195,36,5,'文字列スライス','','>>> msg = \"Hello, World!\"\n>>> print(msg[2:5])\nllo','文字列のスライス','llo','easy','[\"python\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(196,36,5,'リスト','','mylist = []\nmylist.append(1)\nmylist.append(2)\nfor item in mylist:\n    print(item) # prints out 1,2','リスト操作','1\n2','easy','[\"python\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(197,36,5,'If Else','if-else','num = 200\nif num > 0:\n    print(\"num is greater than 0\")\nelse:\n    print(\"num is not greater than 0\")','If-else文','num is greater than 0','easy','[\"python\", \"basics\"]',0,0,6,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(198,36,5,'ループ','','for item in range(6):\n    if item == 3: break\n    print(item)\nelse:\n    print(\"Finally finished!\")','breakとelseを使ったForループ','0\n1\n2','easy','[\"python\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(199,36,5,'関数','','>>> def my_function():\n...     print(\"Hello from a function\")\n...\n>>> my_function()\nHello from a function','関数の定義と呼び出し','Hello from a function','easy','[\"python\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(200,36,5,'ファイル処理','','with open(\"myfile.txt\", \"r\", encoding=\'utf8\') as file:\n    for line in file:\n        print(line)','ファイルを行ごとに読み込む',NULL,'easy','[\"python\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(201,36,5,'算術演算','','result = 10 + 30 # => 40\nresult = 40 - 10 # => 30\nresult = 50 * 5  # => 250\nresult = 16 / 4  # => 4.0 (Float Division)\nresult = 16 // 4 # => 4 (Integer Division)\nresult = 25 % 2  # => 1\nresult = 5 ** 3  # => 125','算術演算子',NULL,'easy','[\"python\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(202,36,5,'複合代入','','counter = 0\ncounter += 10           # => 10\ncounter = 0\ncounter = counter + 10  # => 10\n\nmessage = \"Part 1.\"\n\n# => Part 1.Part 2.\nmessage += \"Part 2.\"','複合代入演算子',