4})\n>>> a.keys()\ndict_keys([\'one\', \'two\', \'three\', \'four\'])\n>>> a[\'four\']\n4','辞書操作','1\ndict_keys([\'one\', \'two\', \'three\'])\ndict_values([1, 2, 3])\ndict_keys([\'one\', \'two\', \'three\', \'four\'])\n4','easy','[\"python\", \"basics\"]',0,0,7,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(211,37,5,'キャスト - 整数','','x = int(1)   # x will be 1\ny = int(2.8) # y will be 2\nz = int(\"3\") # z will be 3','整数への型キャスト',NULL,'easy','[\"python\", \"basics\"]',0,0,8,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(212,37,5,'キャスト - 浮動小数点','','x = float(1)     # x will be 1.0\ny = float(2.8)   # y will be 2.8\nz = float(\"3\")   # z will be 3.0\nw = float(\"4.2\") # w will be 4.2','浮動小数点への型キャスト',NULL,'easy','[\"python\", \"basics\"]',0,0,9,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(213,37,5,'キャスト - 文字列','','x = str(\"s1\") # x will be \'s1\'\ny = str(2)    # y will be \'2\'\nz = str(3.0)  # z will be \'3.0\'','文字列への型キャスト',NULL,'easy','[\"python\", \"basics\"]',0,0,10,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(214,38,5,'ヒープ','','import heapq\n\nmyList = [9, 5, 4, 1, 3, 2]\nheapq.heapify(myList) # turn myList into a Min Heap\nprint(myList)    # => [1, 3, 2, 5, 9, 4]\nprint(myList[0]) # first value is always the smallest in the heap\n\nheapq.heappush(myList, 10) # insert 10\nx = heapq.heappop(myList)  # pop and return smallest item\nprint(x)                   # => 1','ヒープ操作','[1, 3, 2, 5, 9, 4]\n1\n1','medium','[\"python\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(215,38,5,'最小ヒープを使った最大ヒープ','','myList = [9, 5, 4, 1, 3, 2]\nmyList = [-val for val in myList] # multiply by -1 to negate\nheapq.heapify(myList)\n\nx = heapq.heappop(myList)\nprint(-x) # => 9 (making sure to multiply by -1 again)','最小ヒープを最大ヒープとして使用','9','medium','[\"python\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(216,38,5,'スタックとキュー','','from collections import deque\n\nq = deque()          # empty\nq = deque([1, 2, 3]) # with values\n\nq.append(4)     # append to right side\nq.appendleft(0) # append to left side\nprint(q)    # => deque([0, 1, 2, 3, 4])\n\nx = q.pop() # remove & return from right\ny = q.popleft() # remove & return from left\nprint(x)    # => 4\nprint(y)    # => 0\nprint(q)    # => deque([1, 2, 3])\n\nq.rotate(1) # rotate 1 step to the right\nprint(q)    # => deque([3, 1, 2])','スタックとキューのためのDeque','deque([0, 1, 2, 3, 4])\n4\n0\ndeque([1, 2, 3])\ndeque([3, 1, 2])','medium','[\"python\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(217,39,5,'配列風','','>>> hello = \"Hello, World\"\n>>> print(hello[1])\ne\n>>> print(hello[-1])\nd','文字列の文字にアクセス','e\nd','easy','[\"python\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(218,39,5,'ループ','','>>> for char in \"foo\":\n...     print(char)\nf\no\no','文字列をループ処理','f\no\no','easy','[\"python\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(219,39,5,'文字列スライス','','>>> msg = \"Hello, World!\"\n>>> print(msg[2:5])\nllo\n>>> print(msg[:5])\nHello\n>>> print(msg[7:])\nWorld!\n>>> print(msg[-6:-1])\nWorld','文字列スライスの例','llo\nHello\nWorld!\nWorld','easy','[\"python\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(220,39,5,'文字列メソッド','','text = \"Hello World\"\nprint(text.upper())      # => HELLO WORLD\nprint(text.lower())      # => hello world\nprint(text.strip())      # => Hello World\nprint(text.replace(\"H\", \"J\"))  # => Jello World\nprint(text.split(\" \"))   # => [\'Hello\', \'World\']','一般的な文字列メソッド','HELLO WORLD\nhello world\nHello World\nJello World\n[\'Hello\', \'World\']','easy','[\"python\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(221,40,5,'位置引数','','def positional_args(x, y, z):\n    return x, y, z\n\n# => (1, 2, 3)\npositional_args(1, 2, 3)','位置による関数引数','(1, 2, 3)','easy','[\"python\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(222,40,5,'キーワード引数','','def keyword_args(**kwargs):\n    return kwargs\n\n# => {\"big\": \"foot\", \"loch\": \"ness\"}\nkeyword_args(big=\"foot\", loch=\"ness\")','**kwargsを使ったキーワード引数','{\"big\": \"foot\", \"loch\": \"ness\"}','medium','[\"python\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(223,40,5,'複数の戻り値','','def swap(x, y):\n    return y, x\n\nx = 1\ny = 2\nx, y = swap(x, y)  # => x = 2, y = 1','複数の値を返す',NULL,'easy','[\"python\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(224,40,5,'デフォルト値','','def add(x, y=10):\n    return x + y\n\nadd(5)      # => 15\nadd(5, 20)  # => 25','デフォルトパラメータ値','15\n25','easy','[\"python\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(225,40,5,'無名関数','','# => True\n(lambda x: x > 2)(3)\n\n# => 5\n(lambda x, y: x ** 2 + y ** 2)(2, 1)','Lambda関数','True\n5','medium','[\"python\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(226,41,5,'モジュールのインポート','','import math\nprint(math.sqrt(16))  # => 4.0','モジュール全体のインポート','4.0','easy','[\"python\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(227,41,5,'モジュールから','','from math import ceil, floor\nprint(ceil(3.7))   # => 4.0\nprint(floor(3.7))  # => 3.0','特定の関数のインポート','4.0\n3.0','easy','[\"python\", \"basics\"]',0,0,2,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(228,41,5,'すべてインポート','','from math import *','モジュールからすべてをインポート',NULL,'easy','[\"python\", \"basics\"]',0,0,3,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(229,41,5,'モジュールの短縮','','import math as m\n\n# => True\nmath.sqrt(16) == m.sqrt(16)','モジュールのエイリアス',NULL,'easy','[\"python\", \"basics\"]',0,0,4,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(230,41,5,'関数と属性','','import math\ndir(math)','モジュールの関数と属性の一覧',NULL,'easy','[\"python\", \"basics\"]',0,0,5,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(231,42,5,'ファイル読み込み - 行ごと','','with open(\"myfile.txt\") as file:\n    for line in file:\n        print(line)','ファイルを行ごとに読み込む',NULL,'easy','[\"python\", \"basics\"]',0,0,1,1,'2026-01-24 16:35:24','2026-01-24 16:35:24'),(232,42,5,'