L,'https://laravel.com/docs',NULL,NULL,NULL,NULL,NULL,NULL,'[\"#laravel\", \"#php\", \"#documentation\", \"#resources\", \"#tutorial\"]',NULL,NULL,1,'2026-01-02 00:00:00','2026-02-14',4,NULL,20,0,0,'2026-01-08 16:35:31','2026-01-12 16:35:31'),(82,1,92,'LeetCode - Practice Problems','resource_link','LeetCode - コーディング面接の練習問題集',NULL,'https://leetcode.com',NULL,NULL,NULL,NULL,NULL,NULL,'[\"#leetcode\", \"#interview-prep\", \"#algorithm\", \"#resources\", \"#problem-solving\"]',NULL,NULL,3,'2025-12-27 00:00:00','2026-01-25',3,NULL,47,0,0,'2026-01-23 16:35:31','2026-01-10 16:35:31');
