参照渡し.code:
void swap(int& a, int& b) {
    int temp = a;
    a = b;
    b = temp;
}

int x = 5, y = 10;
swap(x, y);
// x = 10, y = 5

ポインタ渡し.code:
void increment(int* ptr) {
    (*ptr)++;
}

int num = 5;
increment(&num);
// num is now 6

