テーブルの作成.code:
CREATE TABLE t (
     id    INT,
     name  VARCHAR DEFAULT NOT NULL,
     price INT DEFAULT 0
     PRIMARY KEY(id)
);

主キー.code:
CREATE TABLE t(
    c1 INT, c2 INT, c3 VARCHAR,
    PRIMARY KEY (c1,c2)
);

外部キー.code:
CREATE TABLE t1(
    c1 INT PRIMARY KEY,  
    c2 INT,
    FOREIGN KEY (c2) REFERENCES t2(c2)
);

UNIQUE制約.code:
CREATE TABLE t(
    c1 INT, c1 INT,
    UNIQUE(c2,c3)
);

CHECK制約.code:
CREATE TABLE t(
  c1 INT, c2 INT,
  CHECK(c1> 0 AND c1 >= c2)
);

NOT NULL制約.code:
CREATE TABLE t(
     c1 INT PRIMARY KEY,
     c2 VARCHAR NOT NULL
);

SELECTからINSERT.code:
INSERT INTO t1(column_list)
SELECT column_list
FROM t2;

CHECK OPTION付きビュー.code:
CREATE VIEW v(c1,c2) 
AS
SELECT c1, c2
FROM t;
WITH [CASCADED | LOCAL] CHECK OPTION;
