foreach ($colors as $color) {
    echo "Do you like $color?\n";
}

戻り値の型.code:
// Basic return type declaration
function sum($a, $b): float {
    return $a + $b;
}
function get_item(): string {
    return "item";
}

class C {}
// Returning an object
function getC(): C { 
    return new C; 
}

デフォルトパラメータ.code:
function coffee($type = "cappuccino")
{
    return "Making a cup of $type.\n";
}
echo coffee();
echo coffee(null);
echo coffee("espresso");

アロー関数.code:
$y = 1;

$fn1 = fn($x) => $x + $y;

// equivalent to using $y by value:
$fn2 = function ($x) use ($y) {
    return $x + $y;
};
echo $fn1(5);
echo $fn2(5);

コンストラクタ.code:
class Student {
    public function __construct($name) {
        $this->name = $name;
    }
    public function print() {
        echo "Name: " . $this->name;
    }
}
$alex = new Student("Alex");
$alex->print();

継承.code:
class ExtendClass extends SimpleClass
{
    // Redefine the parent method
    function displayVar()
    {
        echo "Extending class\n";
        parent::displayVar();
    }
}

$extended = new ExtendClass();
$extended->displayVar();

クラス変数.code:
class MyClass
{
    const MY_CONST       = 'value';
    static $staticVar    = 'static';

    // Visibility
    public static $var1  = 'pubs';
    private static $var2 = 'pris';
    protected static $var3 = 'pros';
}

// Access statically
echo MyClass::MY_CONST;
echo MyClass::$staticVar;

インターフェース.code:
interface Foo 
{
    public function doSomething();
}
interface Bar
{
    public function doSomethingElse();
}
class Cls implements Foo, Bar 
{
    public function doSomething() {}
    public function doSomethingElse() {}
}

