package main

import "fmt"

func main() {
    // Type conversion
    var i int = 42
    var f float64 = float64(i)
    var u uint = uint(f)

    fmt.Printf("i=%d, f=%.2f, u=%d\n", i, f, u)

    // Constants
    const Pi = 3.14159
    const (
        StatusOK = 200
        StatusNotFound = 404
        StatusError = 500
    )

    // iota (sequential constants)
    const (
        Sunday = iota     // 0
        Monday            // 1
        Tuesday           // 2
        Wednesday         // 3
        Thursday          // 4
        Friday            // 5
        Saturday          // 6
    )

    fmt.Println("Monday:", Monday)
    fmt.Println("Friday:", Friday)

    // Typed constant vs untyped constant
    const TypedConst int = 100
    const UntypedConst = 100

    var x int32 = UntypedConst     // OK
    // var y int32 = TypedConst    // Error: cannot use TypedConst (type int) as type int32
}

