)

func sayHello(name string) {
    for i := 0; i < 3; i++ {
        fmt.Printf("Hello, %s!\n", name)
        time.Sleep(100 * time.Millisecond)
    }
}

func main() {
    // Normal function call (synchronous)
    // sayHello("Alice")

    // Goroutine (concurrent execution)
    go sayHello("Alice")
    go sayHello("Bob")

    // Wait so main goroutine doesn't finish
    time.Sleep(1 * time.Second)
    fmt.Println("Done")
}

