package main

import "fmt"

func sum(numbers []int, ch chan int) {
    total := 0
    for _, num := range numbers {
        total += num
    }
    ch <- total  // Gửi vào channel
}

func main() {
    numbers := []int{1, 2, 3, 4, 5, 6}

    ch := make(chan int)

    // Tính toán đồng thời với 2 goroutines
    go sum(numbers[:len(numbers)/2], ch)
    go sum(numbers[len(numbers)/2:], ch)

    // Nhận 2 kết quả
    result1, result2 := <-ch, <-ch

    fmt.Println("Total:", result1+result2)  // 21
}

