// main.go - REST API sử dụng Gin
package main

import (
    "github.com/gin-gonic/gin"
    "net/http"
    "strconv"
)

type Book struct {
    ID     int    `json:"id"`
    Title  string `json:"title"`
    Author string `json:"author"`
    Year   int    `json:"year"`
}

var books = []Book{
    {ID: 1, Title: "The Go Programming Language", Author: "Alan Donovan", Year: 2015},
    {ID: 2, Title: "Learning Go", Author: "Jon Bodner", Year: 2021},
}

func main() {
    router := gin.Default()

    // CORS middleware
    router.Use(corsMiddleware())

    // Routes
    router.GET("/api/books", getBooks)
    router.GET("/api/books/:id", getBook)
    router.POST("/api/books", createBook)
    router.PUT("/api/books/:id", updateBook)
    router.DELETE("/api/books/:id", deleteBook)

    router.Run(":8080")
}

// GET /api/books
func getBooks(c *gin.Context) {
    c.JSON(http.StatusOK, gin.H{"data": books})
}

// GET /api/books/:id
func getBook(c *gin.Context) {
    id, _ := strconv.Atoi(c.Param("id"))

    for _, book := range books {
        if book.ID == id {
            c.JSON(http.StatusOK, gin.H{"data": book})
            return
        }
    }

    c.JSON(http.StatusNotFound, gin.H{"error": "Book not found"})
}

// POST /api/books
func createBook(c *gin.Context) {
    var newBook Book

    if err := c.ShouldBindJSON(&newBook); err != nil {
        c.JSON(http.StatusBadRequest, gin.H{"error": err.Error()})
        return
    }

    newBook.ID = len(books) + 1
    books = append(books, newBook)

    c.JSON(http.StatusCreated, gin.H{"data": newBook})
}

// PUT /api/books/:id
func updateBook(c *gin.Context) {
    id, _ := strconv.Atoi(c.Param("id"))

    var updated Book
    if err := c.ShouldBindJSON(&updated); err != nil {
        c.JSON(http.StatusBadRequest, gin.H{"error": err.Error()})
        return
    }

    for i, book := range books {
        if book.ID == id {
            books[i].Title = updated.Title
            books[i].Author = updated.Author
            books[i].Year = updated.Year
            c.JSON(http.StatusOK, gin.H{"data": books[i]})
            return
        }
    }

    c.JSON(http.StatusNotFound, gin.H{"error": "Book not found"})
}

// DELETE /api/books/:id
func deleteBook(c *gin.Context) {
    id, _ := strconv.Atoi(c.Param("id"))

    for i, book := range books {
        if book.ID == id {
            books = append(books[:i], books[i+1:]...)
            c.JSON(http.StatusOK, gin.H{"message": "Book deleted"})
            return
        }
    }

    c.JSON(http.StatusNotFound, gin.H{"error": "Book not found"})
}

func corsMiddleware() gin.HandlerFunc {
    return func(c *gin.Context) {
        c.Writer.Header().Set("Access-Control-Allow-Origin", "*")
        c.Writer.Header().Set("Access-Control-Allow-Methods", "GET,POST,PUT,DELETE,OPTIONS")
        c.Writer.Header().Set("Access-Control-Allow-Headers", "Content-Type")

        if c.Request.Method == "OPTIONS" {
            c.AbortWithStatus(http.StatusNoContent)
            return
        }

        c.Next()
    }
}

